    def retranslateUi(self, MainWindow):
        _translate = QtCore.QCoreApplication.translate
        MainWindow.setWindowTitle(_translate("MainWindow", "All Timzone DateTime"))
        self.date_text.setPlaceholderText(_translate("MainWindow", "hello"))
        self.time_text.setPlaceholderText(_translate("MainWindow", "hello"))

    def show_datetime(self):
        
        tz=self.timezone_combo.currentText() # get current selected timezone from combo box
        
        while True:
            QtWidgets.QApplication.processEvents()
            # calculate est datetime
            tz_datetime_obj = datetime.now(pytz.timezone(tz))
            tz_date = tz_datetime_obj.strftime('%d %B %Y')
            tz_time = tz_datetime_obj.strftime('%I:%M:%S %p')

            # show est datetime
            self.date_text.setText(tz_date)
            self.time_text.setText(tz_time)

    def show_timezone_on_ui(self):
        # pprint(pytz.all_timezones)
        self.timezone_combo.addItems(list(pytz.all_timezones))

